import { useState } from "react";
import Card from "./Card";
import classes from "./AddUser.module.css";
import Button from "./Button";
import ErrorModal from './ErrorModal';
const AddUser = (props) => {
  const [username, setUsername] = useState("");
  const [age, setAge] = useState('');


  const nameHandler=(event)=>{
    // console.log(event.target.value);
    setUsername(event.target.value);
  }
  const ageHandler=(event)=>{
    setAge(event.target.value);
  }



  const addUserHandler = (event) => {
    event.preventDefault();
    if (username.trim().length===0 || age.trim().length===0 )
        return;
    if (+age<1)
        return;
    props.onSubmit({key:Math.random().toString(),username:username,age:age});

    setUsername('');
    setAge('');
  };
  return (
      <div>
      <ErrorModal title="An error" />
    <Card className={classes.input}>
      <form onSubmit={addUserHandler}>
        <label htmlFor="username">Username</label>
        <input id="username" type="text" onChange={nameHandler} value={username} />
        <label htmlFor="age">Age (years)</label>
        <input id="age" type="number" onChange={ageHandler} value={age}/>
        <Button type="submit">Add User</Button>
      </form>
    </Card>
    </div>
  );
};

export default AddUser;
